import React ,{useState,useCallback,useEffect} from "react";
import { useSocket } from "../context/SocketProvider";
import {useNavigate} from "react-router-dom";
import {Box,Button,styled} from "@mui/material";

const WholeBody=styled(Box)`
background:black,
width:100%,
height:100%,
`

const LobbyScreen=()=>{
    
    const [email,setEmail]=useState("");
    const [room,setRoom]=useState("");
    const socket= useSocket();

    const navigate=useNavigate()

    const handleSubmitForm = useCallback((e)=>{
        e.preventDefault();
        socket.emit("room:join",{email,room});
    },[email,room,socket]);

    const handleJoinRoom=useCallback((data)=>{
        const {email,room}=data;
        navigate(`/room/ ${room}`);

    },[navigate]);

    useEffect(()=>{
        socket.on("room:join",handleJoinRoom);
        return ()=>{
            socket.off('room:join',handleJoinRoom)          //off isliye kar rahe hai because isme hamaara components baar baar render hota hai and we don't want ki hamaara component baar baar listen ho

        };
    },[socket,handleJoinRoom]);

    return(
        <WholeBody>
            <h1>Lobby</h1>
            <form onSubmit={handleSubmitForm}>
                <label htmlFor="email">Email Id</label>
                <input type="email" id="email" value={email} onChange={(e)=>setEmail(e.target.value)}/>
                <br/>
                <label htmlFor="room">Room Number</label>
                <input type="text" id="room" value={room} onChange={(e)=>setRoom(e.target.value)}/>
                <br/>
                <Button variant="contained">Join</Button>
            </form>
        </WholeBody>
    );
};

export default LobbyScreen;
