import React, {useEffect,useCallback,useState} from "react";
import { useSocket } from "../context/SocketProvider";
import ReactPlayer from "react-player";
import peer from "../Services/peer";

const RoomPage=()=>{
    const socket=useSocket();
    const [remoteSocketId,setRemoteSocketId]=useState(null)
    const [myVideo,setmyVideo]=useState();
    const [remoteStream,setRemoteStream]=useState();
    
    const handleUserJoined=useCallback(({email,id})=>{
        console.log(`Email ${email} joined room`);
        setRemoteSocketId(id);
    },[]);
    
    const handleCallUser=useCallback(async()=>{
        const stream=await navigator.mediaDevices.getUserMedia({audio:true,video:true});
        const offer=await peer.getOffer();          //in this what we are doing is humne khud ki stream ko on kara and khud ki ek offer banayi jo hum doosre bande ko bhej rahe hai
        socket.emit("user:call",{to:remoteSocketId,offer});
        setmyVideo(stream);
    },[remoteSocketId,socket]);
    
    const handleIncomingCall = useCallback(async({from,offer})=>{
        setRemoteSocketId(from);
        const stream=await navigator.mediaDevices.getUserMedia({audio:true,video:true});
        setmyVideo(stream);
        console.log(`Incoming Call`,from,offer);
        const ans=await peer.getAnswer(offer);
        socket.emit("call:accepted",{to:from,ans});

    },[socket]);

    const sendStreams=useCallback(()=>{
        for(const track of myVideo.getTracks()){
            peer.peer.addTrack(track,myVideo);
        }
    },[myVideo]);

    const handleCallAccepted=useCallback(({from,ans})=>{
        peer.setLocalDescription(ans);
        console.log("call:accepted!");
        sendStreams();
        
    },[sendStreams]);


    const handleNegoNeeded=useCallback(async ()=>{
        const offer=await peer.getOffer();
        socket.emit("peer:nego:needed",{offer,to:remoteSocketId});

    },[remoteSocketId,socket]);

    useEffect(()=>{
        peer.peer.addEventListener("negotiationneeded",handleNegoNeeded);
        return()=>{
            peer.peer.removeEventListener("negotiationneeded",handleNegoNeeded);
        };
    },[handleNegoNeeded]);

    const handleNegoNeedIncoming=useCallback(async({from,offer})=>{
        const ans=await peer.getAnswer(offer);
        socket.emit("peer:nego:done",{to:from,ans});
    },[socket]);

    const handleNegoNeedFinal=useCallback(async({ans})=>{
        await peer.setLocalDescription(ans)
    },[]);

    useEffect(()=>{
        peer.peer.addEventListener("track",async(ev)=>{
            const remoteStream=ev.streams;
            console.log("GOT TRACKS");
            setRemoteStream(remoteStream[0]); 
        });
    },[]);

    useEffect(()=>{
        socket.on("user:joined",handleUserJoined);
        socket.on("incoming:call",handleIncomingCall);
        socket.on("call:accepted",handleCallAccepted);
        socket.on("peer:nego:needed",handleNegoNeedIncoming);
        socket.on("peer:nego:final",handleNegoNeedFinal);
        
        return ()=>{
            socket.off("user:joined",handleUserJoined);
            socket.off("incoming:call",handleIncomingCall);
            socket.off("call:accepted",handleCallAccepted);
            socket.off("peer:nego:needed",handleNegoNeedIncoming);
            socket.off("peer:nego:final",handleNegoNeedFinal);

        }
    },[socket,handleUserJoined,handleIncomingCall,handleCallAccepted,handleNegoNeedIncoming,handleNegoNeedFinal]);



    return(
        <div className="roomfull">
            <h1 style={{fontSize:"100px",color:"white"}}>Room <span style={{color:"#ededed"}}>Page</span></h1>
            <h4 style={{fontSize:"50px",color:"#ededed",marginTop:"40px"}}>{remoteSocketId ? "Connected" : "wating for other person to join"}</h4>
            {remoteSocketId === null && (
      <div className="buffering-spinner"></div>
    )}
            {myVideo && <button onClick={sendStreams}>Send Stream</button>}
            {
                remoteSocketId && <button onClick={handleCallUser}>CALL</button>
            }
            {
                myVideo && (
                    <>
                        <h1>My Stream</h1>
                        <ReactPlayer 
                            playing
                            muted
                            height="100px" 
                            width="200px" 
                            url={myVideo} />
                    </>
                )
            }
            {
                remoteStream && (
                    <>
                        <h1>Remote Stream</h1>
                        <ReactPlayer 
                            playing
                            muted
                            height="100px" 
                            width="200px" 
                            url={remoteStream} />
                    </>
                )
            }
        </div>
    );
};

export default RoomPage;
